import { useState } from "react";
import { SquareValue } from "../../type";
import Square from "../Square/Square";

const Board: React.FC = () => {
  const [currentGame, setCurrentGame] = useState<SquareValue[]>(Array(9).fill(null))
  //[0 1 2 3 4 5 6 7 8]
  const [nextPlayer, setNextPlayer] = useState("X");

  const renderSquare = (value: number) => {
    return <Square clickSquareHandler={() => SquareClick(value)} value={currentGame[value]}></Square>
  }

  const SquareClick = (value: number) => {
    if(currentGame[value] != null) return;
    let currentGameTemp = currentGame.slice();
    if (nextPlayer == "X"){
      currentGameTemp[value] = "X";
      setNextPlayer("O");
    }else {
      currentGameTemp[value] = "O";
      setNextPlayer("X");
    }
    setCurrentGame(currentGameTemp);
  }
  return (
    <div className="board">
      {renderSquare(0)}
      {renderSquare(1)}
      {renderSquare(2)}
      {renderSquare(3)}
      {renderSquare(4)}
      {renderSquare(5)}
      {renderSquare(6)}
      {renderSquare(7)}
      {renderSquare(8)}
    </div>
  )
}

export default Board;
